import axios from 'axios';

const Main = () => {
    const [isLoading, setIsLoading] = useState(true);
    const [fetchTime, setFetchTime] = useState(1);
    const [goldData, setGoldData] = useState({});
    const [coalData, setCoalData] = useState({});
    const [coffeData, setCoffeData] = useState({});
    const [bitcoinData, setBitcoinData] = useState({});
    const [naturalgas, setNaturalgas] = useState({});
    const [platinumData, setPlatinumData] = useState({});
    const [cocoaData, setCocoaData] = useState({});
    const [soybeanoilData, setSoybeanoilData] = useState({});
    const [gasolineData, setGasolineData] = useState({});
    setInterval(() => {
        setFetchTime(fetchTime + 1)
    }, 25000);
    useEffect(async() => {
        await axios.get('http://localhost:4000/data/gold')
        .then(res => {
            setGoldData(res.data)
        })
        .catch(err => {
            console.log(err)
        })
        await axios.get('http://localhost:4000/data/coal')
        .then(res => {
            setCoalData(res.data)
        })
        .catch(err => {
            console.log(err)
        })
        await axios.get('https://api.coingecko.com/api/v3/coins/markets?vs_currency=usd&ids=bitcoin&order=market_cap_desc&per_page=100&page=1&sparkline=false')
        .then(res => {
            setBitcoinData(res.data[0])
        })
        .catch(err => {
            console.log(err)
        });
        await axios.get('http://localhost:4000/data/naturalgas')
        .then(res => {
            setNaturalgas(res.data)
        })
        .catch(err => {
            console.log(err)
        });
        await axios.get('http://localhost:4000/data/coffee')
        .then(res => {
            setCoffeData(res.data)
        })
        .catch(err => {
            console.log(err)
        });
        await axios.get('http://localhost:4000/data/platinum')
        .then(res => {
            setPlatinumData(res.data)
        })
        .catch(err => {
            console.log(err)
        });
        setIsLoading(false);
        await axios.get('http://localhost:4000/data/cocoa')
        .then(res => {
            setCocoaData(res.data)
        })
        .catch(err => {
            console.log(err)
        });
        await axios.get('http://localhost:4000/data/soybeanoil')
        .then(res => {
            setSoybeanoilData(res.data)
        })
        .catch(err => {
            console.log(err)
        });
        await axios.get('http://localhost:4000/data/ethanol')
        .then(res => {
            setGasolineData(res.data)
        })
        .catch(err => {
            console.log(err)
        });
        setIsLoading(false);
    }, [])
    return (
        <div className="main">
            <header className="header">
                <div className="header-up">
                    <p id="title">I N V E S T O A R E N A</p>
                    <div className="search">
                        <form>
                            <input type="text" id="search-input" placeholder="Tap to Search..."/>
                            <button type="submit" id='search-btn'><img src="/images/search-icon.png" id="search-img" alt=""/></button>
                        </form>
                   </div>
                </div>
                <div className="header-down">
                    <div id="modal-header-box">
                        {goldData.chp > 0 + '%' ? <img src="images/progress.png" id="primg"/>:<img src="images/regress.png" id="primg"/>}
                        <p id="title-header-box">Gold</p>
                        <div className="pricechange">
                            <p className={goldData.chp > 0 + '%' ? "text-success mt-3":"text-danger mt-3"} id="modal-header-chp">{goldData.chp}</p>
                            <p className={goldData.ch > 0 ? "text-success mt-3":"text-danger mt-3"} id="modal-header-ch">{goldData.ch}</p>
                            <p id="modal-header-price">{goldData.price}$</p>
                        </div>
                    </div>
                    <div id="modal-header-box">
                        {coffeData.chp > 0 + '%' ? <img src="images/progress.png" id="primg"/>:<img src="images/regress.png" id="primg"/>}
                        <p id="title-header-box">Coffee</p>
                        <div className="pricechange">
                        <p className={coffeData.chp > 0 + '%' ? "text-success mt-3":"text-danger mt-3"} id="modal-header-chp">{coffeData.chp}</p>
                        <p className={coffeData.ch > 0 ? "text-success mt-3":"text-danger mt-3"} id="modal-header-ch">{coffeData.ch}</p>
                        <p id="modal-header-price">{goldData.price}$</p>
                        </div>
                        </div>
                    <div id="modal-header-box">
                        {coalData.chp > 0 + '%' ? <img src="images/progress.png" id="primg"/>:<img src="images/regress.png" id="primg"/>}
                        <p id="title-header-box">Gold</p>
                        <div className="pricechange">
                        <p className={coalData.chp > 0 + '%' ? "text-success mt-3":"text-danger mt-3"} id="modal-header-chp">{coalData.chp}</p>
                        <p className={coalData.ch > 0 ? "text-success mt-3":"text-danger mt-3"} id="modal-header-ch">{coalData.ch}</p>
                        <p id="modal-header-price">{coalData.price}$</p></div></div>
                    <div id="modal-header-box">
                        {goldData.chp > 0 + '%' ? <img src="images/progress.png" id="primg"/>:<img src="images/regress.png" id="primg"/>}
                        <p id="title-header-box">Gold</p>
                        <div className="pricechange">
                        <p className={goldData.chp > 0 + '%' ? "text-success mt-3":"text-danger mt-3"} id="modal-header-chp">{goldData.chp}</p>
                        <p className={goldData.ch > 0 ? "text-success mt-3":"text-danger mt-3"} id="modal-header-ch">{goldData.ch}</p>
                        <p id="modal-header-price">{goldData.price}$</p></div></div>
                    <div id="modal-header-box">
                        {goldData.chp > 0 + '%' ? <img src="images/progress.png" id="primg"/>:<img src="images/regress.png" id="primg"/>}
                        <p id="title-header-box">Gold</p>
                        <div className="pricechange">
                        <p className={goldData.chp > 0 + '%' ? "text-success mt-3":"text-danger mt-3"} id="modal-header-chp">{goldData.chp}</p>
                        <p className={goldData.ch > 0 ? "text-success mt-3":"text-danger mt-3"} id="modal-header-ch">{goldData.ch}</p>
                        <p id="modal-header-price">{goldData.price}$</p></div></div>
                </div>
            </header>
            <div className="center-box">
                <p id="center-box-title1">Top Commodities</p>
                <div id="line"></div>
                <div className="commodities-container">
                    <div className="commodities-row">
                        {isLoading === false&& <div><Link to='/commodities/coffee' id="commodities-title">Coffee</Link>
                        <p id="commodities-price">{coffeData.price} USD</p>
                        <p id="commodities-chp" className={coffeData.chp > 0 + '%' ? "text-success mr-2":"text-danger mr-2"}>{coffeData.chp}</p></div>}
                        {isLoading && <Spinner animation="border" id="loading-commodities"/>}
                    </div>
                </div>
                <div className="commodities-container">
                    <div className="commodities-row">
                    {isLoading === false&& <div><Link to='/commodities/platinum' id="commodities-title">Platinum</Link>
                        <p id="commodities-price">{platinumData.price} USD</p>
                        <p id="commodities-chp" className={platinumData.chp > 0 + '%' ? "text-success mr-2":"text-danger mr-2"}>{platinumData.chp}</p></div>}
                        {isLoading && <Spinner animation="border" id="loading-commodities"/>}
                    </div>
                </div>
                <div className="commodities-container">
                    <div className="commodities-row">
                        {isLoading === false&& <div><Link to='/commodities/rbob-gasoline' id="commodities-title">Cocoa</Link>
                        <p id="commodities-price">{cocoaData.price} USD</p>
                        <p id="commodities-chp" className={cocoaData.chp > 0 + '%' ? "text-success mr-2":"text-danger mr-2"}>{cocoaData.chp}</p></div>}
                        {isLoading && <Spinner animation="border" id="loading-commodities"/>}
                    </div>
                </div>
                <div className="commodities-container">
                    <div className="commodities-row">
                        {isLoading === false&& <div><Link to='/commodities/rbob-gasoline' id="commodities-title">Soybean Oil</Link>
                        <p id="commodities-price">{soybeanoilData.price} USD</p>
                        <p id="commodities-chp" className={cocoaData.chp > 0 + '%' ? "text-success mr-2":"text-danger mr-2"}>{cocoaData.chp}</p></div>}
                        {isLoading && <Spinner animation="border" id="loading-commodities"/>}
                    </div>
                </div>
                <p id="center-box-title1">Commodity Prices</p>
                <div id="line"></div>
            </div>
        </div>
    )
}

export default Main;
