import Axios from 'axios';
import React, { useEffect, useState } from 'react';
import { Spinner } from 'react-bootstrap';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import progress from '../images/progress.png'
import regress from '../images/regress.png';
import Chart from 'react-apexcharts';
import { Link } from 'react-router-dom';
import Sidebar from 'react-sidebar';

const Metalsdetail = () => {
    const { id } = useParams();
    const [fetchTimer, setFetchTimer] = useState(1);
    const [ cryptoData, setCryptoData ] = useState({});
    const [goldData, setGoldData] = useState({});
    const [fullLoaded, setFullLoaded] = useState(true);
    const [coalData, setCoalData] = useState({});
    const [coffeData, setCoffeData] = useState({});
    const [coalChart, setCoalChart] = useState([]);
    const [platinumData, setPlatinumData] = useState({});
    const [gasolineData, setGasolineData] = useState({});
    const [bitcoinData, setBitcoinData] = useState({});
    const [chartData, setChartData] = useState([]);
    const [isLoading, setIsLoading] = useState(true);
    const [sidebarOpen, setSidebarOpen] = useState(false);
    const [success, setSuccess] = useState(false);
    const [err404, setErr404] = useState(false);
    const formatPrice = data => {
        return data.map(el => {
            console.log(el)
        })
    }
    const fetchData = async() => {
        await axios.get('http://investoarena.herokuapp.com/data/ethanol')
        .then(res => {
            setGasolineData(res.data)
        })
        await axios.get(`https://api.coingecko.com/api/v3/coins/markets?vs_currency=usd&order=market_cap_desc&per_page=50&page=1&sparkline=false&ids=${id}`)
        .then(res => {
            setCryptoData(res.data[0])
        })
        .catch(err => {
            setErr404(true);
        })
        await axios.get('http://investoarena.herokuapp.com/data/platinum')
        .then(res => {
            setPlatinumData(res.data)
            setFullLoaded(false);
        })
        await axios.get('http://investoarena.herokuapp.com/data/coffee')
        .then(res => {
            setCoffeData(res.data)
            setFullLoaded(false);
        })
        await axios.get('http://investoarena.herokuapp.com/data/coal')
        .then(res => {
            setCoalData(res.data)
            setFullLoaded(false);
        })
        .catch(err => {
            setErr404(true);
        });
        await axios.get('http://investoarena.herokuapp.com/data/gold')
        .then(res => {
            setGoldData(res.data)
            setFullLoaded(false);
        })
        .catch(err => {
            setIsLoading(false);
            setErr404(true);
        });
        await axios.get('http://investoarena.herokuapp.com/chart/coal/')
        .then(res => {
            setCoalChart(res.data)
            setFullLoaded(false);
        })
        .catch(err => {
            console.log(err)
        });
        await axios.get(`http://investoarena.herokuapp.com/chart/${id}`)
        .then(res => {
            setChartData(res.data)
            setIsLoading(false);
            setFullLoaded(false);
            setSuccess(true);
        })
        .catch(err => {
            setIsLoading(false);
            setFullLoaded(false);
            setErr404(true);
        })
    }
    setInterval(() => {
        setFetchTimer(fetchTimer + 1)
    }, 25000);
    useEffect(() => {
        fetchData();
    }, [])
    const onSetSidebarOpen = (open) => {
        setSidebarOpen(open)
    } 
    return (
        <div className="main-cent">
        <div className="cryptodetail">
            <header className="header" style={{position:"absolute"}}>
                <div className="header-up">
                    <Sidebar
                        sidebar={
                        <div id="sidebar">
                            <img src="/images/close-outline.svg" alt="" id="navbar-close" onClick={() => {
                                setSidebarOpen(false)
                            }}/>
                            <a href="/" id="a-sidebar">
                            <div className="container-sidebar-1">
                                <img src="/images/home-outline.svg" id="img-sidebar" alt=""/>
                                <p id="text-sidebar">Home</p>
                            </div>
                            </a>
                            <a href="/crypto/" id="a-sidebar">
                            <div className="container-sidebar">
                                <img src="/images/logo-bitcoin.svg" id="img-sidebar" alt=""/>
                                <p id="text-sidebar">Crypto</p>
                            </div>
                            </a>
                            <a href="/metals/" id="a-sidebar">
                            <div className="container-sidebar">
                                <img src="/images/metal.png" id="img-sidebar" alt=""/>
                                <p id="text-sidebar">Metals</p>
                            </div>
                            </a>
                            <a href="/gas/" id="a-sidebar">
                            <div className="container-sidebar">
                                <img src="/images/gas.png" id="img-sidebar" alt=""/>
                                <p id="text-sidebar">Gas</p>
                            </div>
                            </a>
                            <a href="/agriculture/" id="a-sidebar">
                            <div className="container-sidebar">
                                <img src="/images/agriculture.png" id="img-sidebar" alt=""/>
                                <p id="text-sidebar">Agriculture</p>
                            </div>
                            </a>
                        </div>
                    }
                        open={sidebarOpen}
                        onSetOpen={onSetSidebarOpen}
                        id="sidebar"
                        styles={{ sidebar: {background: "white", position:'fixed', left:'0px', width:'470px'}}}
                    >
                   </Sidebar>
                    <img id="navbar" src="/images/navbaricon.png" onClick={() => onSetSidebarOpen(true)}/>
                    <Link to='/' id="title">I N V E S T O A R E N A</Link>
                    <div className="search">
                        <form>
                            <input type="text" id="search-input" placeholder="Tap to Search..."/>
                            <button type="submit" id='search-btn'><img src="/images/search-icon.png" id="search-img" alt=""/></button>
                        </form>
                   </div>
                </div>
                <div className="header-down">
                    <div id="modal-header-box" className="modal1-header">
                    {isLoading === false &&
                    <div> 
                        <img id="primg" src={goldData.chp > 0 + '%' ? progress:regress } alt=""/>
                        <p id="title-header-box">Gold</p>
                        <div className="pricechange">
                            <p className={goldData.chp > 0 + '%' ? "text-success mt-3":"text-danger mt-3"} id="modal-header-chp">{goldData.chp}</p>
                            <p className={goldData.ch > 0 ? "text-success mt-3":"text-danger mt-3"} id="modal-header-ch">{goldData.ch}</p>
                            <p id="modal-header-price">{goldData.price}$</p>
                        </div>
                    </div>
                     }
                     {isLoading && <Spinner animation="border"/>}
                    </div>
                    <div id="modal-header-box" className="modal2-header">
                        {isLoading === false &&
                        <div>
                        <img id="primg" src={coffeData.chp > 0 + '%' ? progress:regress } alt=""/>
                        <p id="title-header-box">Coffee</p>
                        <div className="pricechange">
                        <p className={coffeData.chp > 0 + '%' ? "text-success mt-3":"text-danger mt-3"} id="modal-header-chp">{coffeData.chp}</p>
                        <p className={coffeData.ch > 0 ? "text-success mt-3":"text-danger mt-3"} id="modal-header-ch">{coffeData.ch}</p>
                        <p id="modal-header-price">{coffeData.price}$</p>
                        </div>
                        </div>
                        }
                        {isLoading && <Spinner animation="border"/>}
                        </div>
                    <div id="modal-header-box" className="modal3-header">
                        {isLoading === false && <div>
                        <img id="primg" src={coalData.chp > 0 + '%' ? progress:regress } alt=""/>
                        <p id="title-header-box">Coal</p>
                        <div className="pricechange">
                        <p className={coalData.chp > 0 + '%' ? "text-success mt-3":"text-danger mt-3"} id="modal-header-chp">{coalData.chp}</p>
                        <p className={coalData.ch > 0 ? "text-success mt-3":"text-danger mt-3"} id="modal-header-ch">{coalData.ch}</p>
                        <p id="modal-header-price">{coalData.price}$</p></div></div>}
                     {isLoading && <Spinner animation="border"/>}</div>
                    <div id="modal-header-box" className="modal4-header">
                        {isLoading === false && <div>
                        <img id="primg" src={platinumData.chp > 0 + '%' ? progress:regress } alt=""/>
                        <p id="title-header-box">Platinum</p>
                        <div className="pricechange">
                        <p className={platinumData.chp > 0 + '%' ? "text-success mt-3":"text-danger mt-3"} id="modal-header-chp">{platinumData.chp}</p>
                        <p className={platinumData.ch > 0 ? "text-success mt-3":"text-danger mt-3"} id="modal-header-ch">{platinumData.ch}</p>
                        <p id="modal-header-price">{platinumData.price}$</p></div></div>}
                     {isLoading && <Spinner animation="border"/>}</div>
                    <div id="modal-header-box" className="modal5-header">
                        {isLoading === false && <div>
                        <img id="primg" src={gasolineData.chp > 0 + '%' ? progress:regress } alt=""/>
                        <p id="title-header-box">Ethanol</p>
                        <div className="pricechange">
                        <p className={gasolineData.chp > 0 + '%' ? "text-success mt-3":"text-danger mt-3"} id="modal-header-chp">{gasolineData.chp}</p>
                        <p className={gasolineData.ch > 0 ? "text-success mt-3":"text-danger mt-3"} id="modal-header-ch">{gasolineData.ch}</p>
                        <p id="modal-header-price">{gasolineData.price}$</p></div></div>}
                     {isLoading && <Spinner animation="border"/>}</div>
                </div>
            </header>
            <div className="center-box-detail">
                <div className="info-detail">
                <div className="blueline"></div>
                <p id="title-detail">
                    {cryptoData.name}
                </p>
                <div id="pricing-info">
                    <p id="price-detail">
                        {cryptoData.current_price}
                    </p>
                    <p id="ch-detail" className={cryptoData.price_change_24h > 0 ? "text-success mr-2": "text-danger mr-2"}>
                        {cryptoData.price_change_24h}$
                    </p>
                    <p id="chp-detail" className={cryptoData.price_change_24h > 0 ? "text-success mr-2": "text-danger mr-2"}>
                        {cryptoData.price_change_percentage_24h}%
                    </p>
                </div>
            </div>
            <Chart id="myChart"
              options={{
                chart: {
                  id: "basic-bar"
                },
                xaxis: {
                    type:'datetime'
                }
              }}
              series={[
                {
                  name: "series-1",
                  data: formatPrice([chartData]),
                }
              ]}
              type="line"
            />
            <div className="info-modals-detail">
                <div className="modal1-info">
                    <p id="modal-low-price">
                        Day Low
                    </p>
                    <p id="modal-high-price">
                        Day High
                    </p>
                    <p id="modal-low-price-value">
                        {cryptoData.low_24h}
                    </p>
                    <p id="modal-high-price-value">
                        {cryptoData.high_24h}
                    </p>
                </div>
                <div className="modal2-info">
                    <p id="modal-marketcap-rank">
                        CoinMarketCap Ranking
                    </p>
                    <p id="modal-marketcap-price">
                        CoinMarketCap
                    </p>
                    <p id="modal-marketcap-rank-value">
                        {cryptoData.market_cap_rank}
                    </p>
                    <p id="modal-marketcap-price-value">
                        {cryptoData.market_cap}
                    </p>
                </div>
            </div>
        </div>
        </div>
        </div>
    )
}

export default Metalsdetail;
