import React, {useState, useEffect} from 'react';
import { Spinner } from 'react-bootstrap';
import { Link } from 'react-router-dom';
import Cryptolist from '../components/Cryptolist';
import axios from 'axios';

const Main = () => {
    const [isLoading, setIsLoading] = useState(true);
    const [fetchTime, setFetchTime] = useState(1);
    const [goldData, setGoldData] = useState({});
    const [coalData, setCoalData] = useState({});
    const [coffeData, setCoffeData] = useState({});
    const [bitcoinData, setBitcoinData] = useState({});
    const [aluminum, setAluminum] = useState({});
    const [naturalgas, setNaturalgas] = useState({});
    const [gasoline, setGasoline] = useState({});
    const [palladiumData, setPalladiumData] = useState({});
    const [heatingoil, setHeatingoil] = useState({}); 
    const [platinumData, setPlatinumData] = useState({});
    const [cocoaData, setCocoaData] = useState({});
    const [soybeanoilData, setSoybeanoilData] = useState({});
    const [gasolineData, setGasolineData] = useState({});
    const [silverData, setSilverData] = useState({});
    const [crypto, setCrypto] = useState([]);
    setInterval(() => {
        setFetchTime(fetchTime + 1)
    }, 25000);
    useEffect(async() => {
        await axios.get('http://localhost:4000/data/gold')
        .then(res => {
            setGoldData(res.data)
        })
        .catch(err => {
            console.log(err)
        })
        await axios.get('http://localhost:4000/data/coal')
        .then(res => {
            setCoalData(res.data)
        })
        .catch(err => {
            console.log(err)
        })
        await axios.get('https://api.coingecko.com/api/v3/coins/markets?vs_currency=usd&order=market_cap_desc&per_page=15&page=1&sparkline=false')
        .then(res => {
            setCrypto(res.data)
        })
        .catch(err => {
            console.log(err)
        });
        await axios.get('http://localhost:4000/data/naturalgas')
        .then(res => {
            setNaturalgas(res.data)
        })
        .catch(err => {
            console.log(err)
        });
        await axios.get('http://localhost:4000/data/coffee')
        .then(res => {
            setCoffeData(res.data)
        })
        .catch(err => {
            console.log(err)
        });
        await axios.get('http://localhost:4000/data/platinum')
        .then(res => {
            setPlatinumData(res.data)
        })
        .catch(err => {
            console.log(err)
        });
        setIsLoading(false);
        await axios.get('http://localhost:4000/data/cocoa')
        .then(res => {
            setCocoaData(res.data)
        })
        .catch(err => {
            console.log(err)
        });
        await axios.get('http://localhost:4000/data/soybeanoil')
        .then(res => {
            setSoybeanoilData(res.data)
        })
        .catch(err => {
            console.log(err)
        });
        await axios.get('http://localhost:4000/data/ethanol')
        .then(res => {
            setGasolineData(res.data)
        })
        .catch(err => {
            console.log(err)
        });
        await axios.get('http://localhost:4000/data/heatingoil')
        .then(res => {
            setHeatingoil(res.data)
        })
        .catch(err => {
            console.log(err)
        });
        await axios.get('http://localhost:4000/data/silver')
        .then(res => {
            setSilverData(res.data)
        })
        .catch(err => {
            console.log(err)
        });
        await axios.get('http://localhost:4000/data/palladium')
        .then(res => {
            setPalladiumData(res.data)
        })
        await axios.get('http://localhost:4000/data/aluminum')
        .then(res => {
            setAluminum(res.data)
        })
        .catch(err => {
            console.log(err)
        });
        await axios.get('http://localhost:4000/data/gasoline')
        .then(res => {
            setGasoline(res.data)
        })
        .catch(err => {
            console.log(err)
        });
        setIsLoading(false);
    }, [])
    return (
        <div className="main">
            <header className="header">
                <div className="header-up">
                    <p id="title">I N V E S T O A R E N A</p>
                    <div className="search">
                        <form>
                            <input type="text" id="search-input" placeholder="Tap to Search..."/>
                            <button type="submit" id='search-btn'><img src="/images/search-icon.png" id="search-img" alt=""/></button>
                        </form>
                   </div>
                </div>
                <div className="header-down">
                    <div id="modal-header-box" className="modal1-header">
                    {isLoading === false &&
                    <div> 
                        <img id="primg" src={goldData.chp > 0 + '%' ? "images/progress.png":"images/regress.png" }/>
                        <p id="title-header-box">Gold</p>
                        <div className="pricechange">
                            <p className={goldData.chp > 0 + '%' ? "text-success mt-3":"text-danger mt-3"} id="modal-header-chp">{goldData.chp}</p>
                            <p className={goldData.ch > 0 ? "text-success mt-3":"text-danger mt-3"} id="modal-header-ch">{goldData.ch}</p>
                            <p id="modal-header-price">{goldData.price}$</p>
                        </div>
                    </div>
                     }
                     {isLoading && <Spinner animation="border"/>}
                    </div>
                    <div id="modal-header-box" className="modal2-header">
                        {isLoading === false &&
                        <div>
                        <img id="primg" src={coffeData.chp > 0 + '%' ? "images/progress.png":"images/regress.png" }/>
                        <p id="title-header-box">Coffee</p>
                        <div className="pricechange">
                        <p className={coffeData.chp > 0 + '%' ? "text-success mt-3":"text-danger mt-3"} id="modal-header-chp">{coffeData.chp}</p>
                        <p className={coffeData.ch > 0 ? "text-success mt-3":"text-danger mt-3"} id="modal-header-ch">{coffeData.ch}</p>
                        <p id="modal-header-price">{coffeData.price}$</p>
                        </div>
                        </div>
                        }
                        {isLoading && <Spinner animation="border"/>}
                        </div>
                    <div id="modal-header-box" className="modal3-header">
                        {isLoading === false && <div>
                        <img id="primg" src={coalData.chp > 0 + '%' ? "images/progress.png":"images/regress.png" }/>
                        <p id="title-header-box">Coal</p>
                        <div className="pricechange">
                        <p className={coalData.chp > 0 + '%' ? "text-success mt-3":"text-danger mt-3"} id="modal-header-chp">{coalData.chp}</p>
                        <p className={coalData.ch > 0 ? "text-success mt-3":"text-danger mt-3"} id="modal-header-ch">{coalData.ch}</p>
                        <p id="modal-header-price">{coalData.price}$</p></div></div>}
                     {isLoading && <Spinner animation="border"/>}</div>
                    <div id="modal-header-box" className="modal4-header">
                        {isLoading === false && <div>
                        <img id="primg" src={platinumData.chp > 0 + '%' ? "images/progress.png":"images/regress.png" }/>
                        <p id="title-header-box">Platinum</p>
                        <div className="pricechange">
                        <p className={platinumData.chp > 0 + '%' ? "text-success mt-3":"text-danger mt-3"} id="modal-header-chp">{platinumData.chp}</p>
                        <p className={platinumData.ch > 0 ? "text-success mt-3":"text-danger mt-3"} id="modal-header-ch">{platinumData.ch}</p>
                        <p id="modal-header-price">{bitcoinData.current_price}$</p></div></div>}
                     {isLoading && <Spinner animation="border"/>}</div>
                    <div id="modal-header-box" className="modal5-header">
                        {isLoading === false && <div>
                        <img id="primg" src={gasolineData.chp > 0 + '%' ? "images/progress.png":"images/regress.png" }/>
                        <p id="title-header-box">Ethanol</p>
                        <div className="pricechange">
                        <p className={gasolineData.chp > 0 + '%' ? "text-success mt-3":"text-danger mt-3"} id="modal-header-chp">{gasolineData.chp}</p>
                        <p className={gasolineData.ch > 0 ? "text-success mt-3":"text-danger mt-3"} id="modal-header-ch">{gasolineData.ch}</p>
                        <p id="modal-header-price">{gasolineData.price}$</p></div></div>}
                     {isLoading && <Spinner animation="border"/>}</div>
                </div>
            </header>
            <div className="center-box">
                <p id="center-box-title1">Commodities Top Performers</p>
                <div id="line"></div>
                <div className="commodities-container">
                    <div className="commodities-row">
                        {isLoading === false&& <div><Link to='/commodities/coffee' id="commodities-title">Coffee</Link>
                        <p id="commodities-price">{coffeData.price} USD</p>
                        <p id="commodities-chp" className={coffeData.chp > 0 + '%' ? "text-success mr-2":"text-danger mr-2"}>{coffeData.chp}</p></div>}
                    </div>
                </div>
                <div className="commodities-container">
                    <div className="commodities-row">
                        {isLoading === false&& <div><Link to='/commodities/cocoa' id="commodities-title">Cocoa</Link>
                        <p id="commodities-price">{cocoaData.price} USD</p>
                        <p id="commodities-chp" className={cocoaData.chp > 0 + '%' ? "text-success mr-2":"text-danger mr-2"}>{cocoaData.chp}</p></div>}
                    </div>
                </div>
                <div className="commodities-container">
                    <div className="commodities-row">
                        {isLoading === false&& <div><Link to='/commodities/heatingoil' id="commodities-title">Heating Oil</Link>
                        <p id="commodities-price">{heatingoil.price} USD</p>
                        <p id="commodities-chp" className={heatingoil.chp > 0 + '%' ? "text-success mr-2":"text-danger mr-2"}>{heatingoil.chp}</p></div>}
                    </div>
                </div>
                <div className="commodities-container">
                    <div className="commodities-row">
                        {isLoading === false&& <div><Link to='/commodities/soybeanoil' id="commodities-title">Soybean Oil</Link>
                        <p id="commodities-price">{soybeanoilData.price} USD</p>
                        <p id="commodities-chp" className={cocoaData.chp > 0 + '%' ? "text-success mr-2":"text-danger mr-2"}>{cocoaData.chp}</p></div>}
                    </div>
                </div>
                <div className="commodities-container">
                    <div className="commodities-row">
                        {isLoading === false&& <div><Link to='/commodities/silver' id="commodities-title">Silver</Link>
                        <p id="commodities-price">{silverData.price} USD</p>
                        <p id="commodities-chp" className={silverData.chp > 0 + '%' ? "text-success mr-2":"text-danger mr-2"}>{silverData.chp}</p></div>}
                    </div>
                </div>
                <p id="center-box-title1">Commodity Prices</p>
                <div id="line"></div>
                <div className="commodities-container">
                    <div className="commodities-row">
                        <p id="commodities-title">Metals</p>
                        <p id="commodities-price">Price</p>
                        <p id="commodities-chp-c">%</p>
                        <p id="commodities-chp-c-c">+/-</p>
                    </div>
                </div>
                <div className="commodities-container">
                    <div className="commodities-row">
                        {isLoading === false&& <div><Link to='/commodities/gold' id="commodities-title">Gold</Link>
                        <p id="commodities-price">{goldData.price} USD</p>
                        <p id="commodities-ch-2" className={goldData.ch > 0 ? "text-sucess mr-2" : "text-danger mr-2"}>{goldData.ch}$</p>
                        <p id="commodities-chp-2" className={goldData.chp > 0 + '%' ? "text-success mr-2":"text-danger mr-2"}>{goldData.chp}</p></div>
                        }
                    </div>
                </div>
                <div className="commodities-container">
                    <div className="commodities-row">
                        {isLoading === false&& <div><Link to='/commodities/silver' id="commodities-title">Silver</Link>
                        <p id="commodities-price">{silverData.price} USD</p>
                        <p id="commodities-ch-2" className={silverData.ch > 0 ? "text-success mr-2" : "text-danger mr-2"}>{silverData.ch}$</p>
                        <p id="commodities-chp-2" className={silverData.chp > 0 + '%' ? "text-success mr-2":"text-danger mr-2"}>{silverData.chp}</p></div>
                        }
                    </div>
                </div>
                <div className="commodities-container">
                    <div className="commodities-row">
                        {isLoading === false&& <div><Link to='/commodities/platinum' id="commodities-title">Platinum</Link>
                        <p id="commodities-price">{platinumData.price} USD</p>
                        <p id="commodities-ch-2" className={platinumData.ch > 0 ? "text-success mr-2" : "text-danger mr-2"}>{platinumData.ch}$</p>
                        <p id="commodities-chp-2" className={platinumData.chp > 0 + '%' ? "text-success mr-2":"text-danger mr-2"}>{platinumData.chp}</p></div>
                        }
                    </div>
                </div>
                <div className="commodities-container">
                    <div className="commodities-row">
                        {isLoading === false&& <div><Link to='/commodities/palladium' id="commodities-title">Palladium</Link>
                        <p id="commodities-price">{palladiumData.price} USD</p>
                        <p id="commodities-ch-2" className={palladiumData.ch > 0 ? "text-success mr-2" : "text-danger mr-2"}>{palladiumData.ch}$</p>
                        <p id="commodities-chp-2" className={palladiumData.chp > 0 + '%' ? "text-success mr-2":"text-danger mr-2"}>{palladiumData.chp}</p></div>
                        }
                    </div>
                </div>
                <div className="commodities-container">
                    <div className="commodities-row">
                        {isLoading === false&& <div><Link to='/commodities/aluminium' id="commodities-title">Aluminium</Link>
                        <p id="commodities-price">{aluminum.price} USD</p>
                        <p id="commodities-ch-2" className={aluminum.ch > 0 ? "text-success mr-2" : "text-danger mr-2"}>{aluminum.ch}$</p>
                        <p id="commodities-chp-2" className={aluminum.chp > 0 + '%' ? "text-success mr-2":"text-danger mr-2"}>{aluminum.chp}</p></div>
                        }
                    </div>
                </div>
                <div className="commodities-container2">
                    <div className="commodities-row">
                        <p id="commodities-title">Energy</p>
                        <p id="commodities-price">Price</p>
                        <p id="commodities-chp-c">%</p>
                        <p id="commodities-chp-c-c">+/-</p>
                    </div>
                </div>
                <div className="commodities-container">
                    <div className="commodities-row">
                        {isLoading === false&& <div><Link to='/commodities/platinum' id="commodities-title">Natural Gas</Link>
                        <p id="commodities-price">{naturalgas.price} USD</p>
                        <p id="commodities-ch-2" className={naturalgas.ch > 0 ? "text-success mr-2" : "text-danger mr-2"}>{naturalgas.ch}$</p>
                        <p id="commodities-chp-2" className={naturalgas.chp > 0 + '%' ? "text-success mr-2":"text-danger mr-2"}>{naturalgas.chp}</p></div>
                        }
                    </div>
                </div>
                <div className="commodities-container">
                    <div className="commodities-row">
                        {isLoading === false&& <div><Link to='/commodities/platinum' id="commodities-title">Ethanol</Link>
                        <p id="commodities-price">{gasolineData.price} USD</p>
                        <p id="commodities-ch-2" className={gasolineData.ch > 0 ? "text-success mr-2" : "text-danger mr-2"}>{gasolineData.ch}$</p>
                        <p id="commodities-chp-2" className={gasolineData.chp > 0 + '%' ? "text-success mr-2":"text-danger mr-2"}>{gasolineData.chp}</p></div>
                        }
                    </div>
                </div>
                <div className="commodities-container">
                    <div className="commodities-row">
                        {isLoading === false&& <div><Link to='/commodities/heatingoil' id="commodities-title">Heating Oil</Link>
                        <p id="commodities-price">{heatingoil.price} USD</p>
                        <p id="commodities-ch-2" className={heatingoil.ch > 0 ? "text-success mr-2" : "text-danger mr-2"}>{heatingoil.ch}$</p>
                        <p id="commodities-chp-2" className={heatingoil.chp > 0 + '%' ? "text-success mr-2":"text-danger mr-2"}>{heatingoil.chp}</p></div>
                        }
                    </div>
                </div>
                <div className="commodities-container">
                    <div className="commodities-row">
                        {isLoading === false&& <div><Link to='/commodities/heatingoil' id="commodities-title">Coal</Link>
                        <p id="commodities-price">{coalData.price} USD</p>
                        <p id="commodities-ch-2" className={coalData.ch > 0 ? "text-success mr-2" : "text-danger mr-2"}>{coalData.ch}$</p>
                        <p id="commodities-chp-2" className={coalData.chp > 0 + '%' ? "text-success mr-2":"text-danger mr-2"}>{coalData.chp}</p></div>
                        }
                    </div>
                </div>
                <div className="commodities-container">
                    <div className="commodities-row">
                        {isLoading === false&& <div><Link to='/commodities/heatingoil' id="commodities-title">RBOB Gasoline</Link>
                        <p id="commodities-price">{gasoline.price} USD</p>
                        <p id="commodities-ch-2" className={gasoline.ch > 0 ? "text-success mr-2" : "text-danger mr-2"}>{gasoline.ch}$</p>
                        <p id="commodities-chp-2" className={gasoline.chp > 0 + '%' ? "text-success mr-2":"text-danger mr-2"}>{gasoline.chp}</p></div>
                        }
                    </div>
                </div>
                <div className="commodities-container">
                    <div className="commodities-row">
                        <p id="commodities-title">Crypto</p>
                        <p id="commodities-price">Price</p>
                        <p id="commodities-chp-c">%</p>
                        <p id="commodities-chp-c-c">+/-</p>
                    </div>
                </div>
                {crypto.map(coin => {
                    return (
                    <Cryptolist name={coin.name} id={coin.id} price={coin.current_price} ch={coin.price_change_24h} chp={coin.price_change_percentage_24h} />
                    )
                })}
                </div>
            </div>
    )
}

export default Main;
