import React, {useState, useEffect} from 'react';
import { Spinner } from 'react-bootstrap';
import { Link } from 'react-router-dom';
import axios from 'axios';

const Main = () => {
    const [isLoading, setIsLoading] = useState(true);
    const [fetchTime, setFetchTime] = useState(1);
    const [goldData, setGoldData] = useState({});
    const [coalData, setCoalData] = useState({});
    const [coffeData, setCoffeData] = useState({});
    const [bitcoinData, setBitcoinData] = useState({});
    const [naturalgas, setNaturalgas] = useState({});
    const [platinumData, setPlatinumData] = useState({});
    const [cocoaData, setCocoaData] = useState({});
    const [soybeanoilData, setSoybeanoilData] = useState({});
    const [gasolineData, setGasolineData] = useState({});
    setInterval(() => {
        setFetchTime(fetchTime + 1)
    }, 25000);
    useEffect(async() => {
        await axios.get('http://localhost:4000/data/gold')
        .then(res => {
            setGoldData(res.data)
        })
        .catch(err => {
            console.log(err)
        })
        await axios.get('http://localhost:4000/data/coal')
        .then(res => {
            setCoalData(res.data)
        })
        .catch(err => {
            console.log(err)
        })
        await axios.get('https://api.coingecko.com/api/v3/coins/markets?vs_currency=usd&ids=bitcoin&order=market_cap_desc&per_page=100&page=1&sparkline=false')
        .then(res => {
            setBitcoinData(res.data[0])
        })
        .catch(err => {
            console.log(err)
        });
        await axios.get('http://localhost:4000/data/naturalgas')
        .then(res => {
            setNaturalgas(res.data)
        })
        .catch(err => {
            console.log(err)
        });
        await axios.get('http://localhost:4000/data/coffee')
        .then(res => {
            setCoffeData(res.data)
        })
        .catch(err => {
            console.log(err)
        });
        await axios.get('http://localhost:4000/data/platinum')
        .then(res => {
            setPlatinumData(res.data)
        })
        .catch(err => {
            console.log(err)
        });
        setIsLoading(false);
        await axios.get('http://localhost:4000/data/cocoa')
        .then(res => {
            setCocoaData(res.data)
        })
        .catch(err => {
            console.log(err)
        });
        await axios.get('http://localhost:4000/data/soybeanoil')
        .then(res => {
            setSoybeanoilData(res.data)
        })
        .catch(err => {
            console.log(err)
        });
        await axios.get('http://localhost:4000/data/ethanol')
        .then(res => {
            setGasolineData(res.data)
        })
        .catch(err => {
            console.log(err)
        });
        setIsLoading(false);
    }, [])
    return (
        <div className="main">
            <header className="header">
                <div className="header-up">
                    <p id="title">I N V E S T O A R E N A</p>
                    <div className="search">
                        <form>
                            <input type="text" id="search-input" placeholder="Tap to Search..."/>
                            <button type="submit" id='search-btn'><img src="/images/search-icon.png" id="search-img" alt=""/></button>
                        </form>
                   </div>
                </div>
                <div className="header-down">
                    <div className="header-modal1">  
                    {isLoading === false &&
                    <div>
                        <p id="header-modal-title-gold">Gold</p>
                        <p id="header-modal-price">{goldData.price}$</p>
                        <div className="pricechange">
                            <p id="header-modal-chp" className={goldData.ch > 0 + '%' ? "text-success mr-2":"text-danger mr-2"}>{goldData.ch}$</p>
                            <p id="header-modal-ch" className={goldData.chp > 0 + '%' + '%' ? "text-success mr-2":"text-danger mr-2"}>{goldData.chp}</p>
                        </div>
                    </div>
                     }
                     {isLoading && <Spinner animation="border"/>}
                    </div>
                    <div className="header-modal2">
                    {isLoading === false && 
                    <div>
                        <p id="header-modal-title">Coal</p>
                        <p id="header-modal-price">{coalData.price}$</p>
                        <div className="pricechange">
                            <p id="header-modal-chp" className={coalData.ch > 0 + '%' ? "text-success mr-2":"text-danger mr-2"}>{coalData.ch}$</p>
                            <p id="header-modal-ch" className={coalData.chp > 0 + '%' ? "text-success mr-2":"text-danger mr-2"}>{coalData.chp}</p>
                        </div>
                    </div>
                    }
                    {isLoading && <Spinner animation="border"/>}
                    </div>
                    <div className="header-modal3">
                        {isLoading === false &&
                        <div>
                        <p id="header-modal-title-bitcoin">Bitcoin</p>
                        <p id="header-modal-price">{bitcoinData.current_price}$</p>
                        <div className="pricechange-bitcoin">
                            <p id="header-modal-chp-bitcoin" className={bitcoinData.price_change_24h > 0 + '%' ? "text-success mr-2":"text-danger mr-2"}>{bitcoinData.price_change_24h}$</p>
                            <p id="header-modal-ch-bitcoin" className={bitcoinData.price_change_percentage_24h > 0 + '%'? "text-success mr-2":"text-danger mr-2"}>{bitcoinData.price_change_percentage_24h}%</p>
                        </div>
                        </div>
                    }
                    {isLoading && <Spinner animation="border"/>}
                    </div>
                    <div className="header-modal4">
                    {isLoading === false &&
                        <div>
                        <p id="header-modal-title">Natural Gas</p>
                        <p id="header-modal-price">{naturalgas.price}$</p>
                        <div className="pricechange">
                            <p id="header-modal-chp" className={naturalgas.ch > 0 + '%' ? "text-success mr-2":"text-danger mr-2"}>{naturalgas.ch}$</p>
                            <p id="header-modal-ch" className={naturalgas.chp > 0 + '%' ? "text-success mr-2":"text-danger mr-2"}>{naturalgas.chp}</p>
                        </div>
                        </div>
                    }
                    {isLoading && <Spinner animation="border"/>}
                    </div>
                    <div className="header-modal5">
                    {isLoading === false &&
                        <div>
                        <p id="header-modal-title-ethanol">Ethanol</p>
                        <p id="header-modal-price-ethanol">{gasolineData.price}$</p>
                        <div className="pricechange-ethanol">
                            <p id="header-modal-chp" className={gasolineData.ch > 0 + '%' ? "text-success mr-2":"text-danger mr-2"}>{gasolineData.ch}$</p>
                            <p id="header-modal-ch" className={gasolineData.chp > 0 + '%' ? "text-success mr-2":"text-danger mr-2"}>{gasolineData.chp}</p>
                        </div>
                        </div>
                    }
                    {isLoading && <Spinner animation="border"/>}
                    </div>
                </div>
            </header>
            <div className="center-box">
                <p id="center-box-title1">Top Commodities</p>
                <div id="line"></div>
                <div className="commodities-container">
                    <div className="commodities-row">
                        {isLoading === false&& <div><Link to='/commodities/coffee' id="commodities-title">Coffee</Link>
                        <p id="commodities-price">{coffeData.price} USD</p>
                        <p id="commodities-chp" className={coffeData.chp > 0 + '%' ? "text-success mr-2":"text-danger mr-2"}>{coffeData.chp}</p></div>}
                        {isLoading && <Spinner animation="border" id="loading-commodities"/>}
                    </div>
                </div>
                <div className="commodities-container">
                    <div className="commodities-row">
                    {isLoading === false&& <div><Link to='/commodities/platinum' id="commodities-title">Platinum</Link>
                        <p id="commodities-price">{platinumData.price} USD</p>
                        <p id="commodities-chp" className={platinumData.chp > 0 + '%' ? "text-success mr-2":"text-danger mr-2"}>{platinumData.chp}</p></div>}
                        {isLoading && <Spinner animation="border" id="loading-commodities"/>}
                    </div>
                </div>
                <div className="commodities-container">
                    <div className="commodities-row">
                        {isLoading === false&& <div><Link to='/commodities/rbob-gasoline' id="commodities-title">Cocoa</Link>
                        <p id="commodities-price">{cocoaData.price} USD</p>
                        <p id="commodities-chp" className={cocoaData.chp > 0 + '%' ? "text-success mr-2":"text-danger mr-2"}>{cocoaData.chp}</p></div>}
                        {isLoading && <Spinner animation="border" id="loading-commodities"/>}
                    </div>
                </div>
                <div className="commodities-container">
                    <div className="commodities-row">
                        {isLoading === false&& <div><Link to='/commodities/rbob-gasoline' id="commodities-title">Soybean Oil</Link>
                        <p id="commodities-price">{soybeanoilData.price} USD</p>
                        <p id="commodities-chp" className={cocoaData.chp > 0 + '%' ? "text-success mr-2":"text-danger mr-2"}>{cocoaData.chp}</p></div>}
                        {isLoading && <Spinner animation="border" id="loading-commodities"/>}
                    </div>
                </div>
                <p id="center-box-title1">Top Commodities</p>
                <div id="line"></div>
            </div>
        </div>
    )
}

export default Main;
