import React, {useState, useEffect} from 'react';
import axios from 'axios';

const Main = () => {
    const [fetchTime, setFetchTime] = useState();
    const [goldData, setGoldData] = useState({});
    useEffect(() => {
        axios.get('http://localhost:4000/data/gold')
        .then(res => {
            setGoldData(res.data)
        })
        .catch(err => {
            console.log(err)
        })
    })
    return (
        <div className="main">
            <header className="header">
                <div className="header-up">
                    <p id="title">I N V E S T O A R E N A</p>
                    <img src="/images/navbaricon.png" alt="" id="navbaricon"/>
                    <div className="search">
                        <form>
                            <input type="text" id="search-input" placeholder="Tap to Search..."/>
                            <button type="submit" id='search-btn'><img src="/images/search-icon.png" id="search-img" alt=""/></button>
                        </form>
                   </div>
                </div>
                <div className="header-down">
                    <div className="header-modal1">
                        <p id="header-modal-title">Gold</p>
                        <p id="header-modal-price">{goldData.price}$</p>
                        <p id="header-modal-chp" className={goldData.chp > 0 ? "text-success mr-2":"text-danger mr-2"}>{goldData.chp}%</p>
                    </div>
                    <div className="header-modal2">
                        
                    </div>
                    <div className="header-modal3">
                        
                    </div>
                    <div className="header-modal4">
                        
                    </div>
                </div>
            </header>
        </div>
    )
}

export default Main;
