import React, {useState, useEffect} from 'react';
import axios from 'axios';

const Main = () => {
    const [fetchTime, setFetchTime] = useState(1);
    const [goldData, setGoldData] = useState({});
    const [coalData, setCoalData] = useState({});
    const [bitcoinData, setBitcoinData] = useState({});
    setInterval(() => {
        setFetchTime(fetchTime + 1)
    }, 15000);
    useEffect(async() => {
        await axios.get('http://localhost:4000/data/gold')
        .then(res => {
            setGoldData(res.data)
        })
        .catch(err => {
            console.log(err)
        })
        await axios.get('http://localhost:4000/data/coal')
        .then(res => {
            setCoalData(res.data)
        })
        .catch(err => {
            console.log(err)
        })
        await axios.get('https://api.coingecko.com/api/v3/coins/markets?vs_currency=usd&ids=bitcoin&order=market_cap_desc&per_page=100&page=1&sparkline=false')
        .then(res => {
            setBitcoinData(res.data[0])
        })
        .catch(err => {
            console.log(err)
        })
    }, [fetchTime])
    return (
        <div className="main">
            <header className="header">
                <div className="header-up">
                    <p id="title">I N V E S T O A R E N A</p>
                    <img src="/images/navbaricon.png" alt="" id="navbaricon"/>
                    <div className="search">
                        <form>
                            <input type="text" id="search-input" placeholder="Tap to Search..."/>
                            <button type="submit" id='search-btn'><img src="/images/search-icon.png" id="search-img" alt=""/></button>
                        </form>
                   </div>
                </div>
                <div className="header-down">
                    <div className="header-modal1">
                        <p id="header-modal-title">Gold</p>
                        <p id="header-modal-price">{goldData.price}$</p>
                        <div className="pricechange">
                            <p id="header-modal-chp" className={goldData.ch > 0 ? "text-success mr-2":"text-danger mr-2"}>{goldData.ch}$</p>
                            <p id="header-modal-ch" className={goldData.chp > 0 ? "text-success mr-2":"text-danger mr-2"}>{goldData.chp}</p>
                        </div>
                    </div>
                    <div className="header-modal2">
                        <p id="header-modal-title">Coal</p>
                        <p id="header-modal-price">{coalData.price}$</p>
                        <div className="pricechange">
                            <p id="header-modal-chp" className={coalData.ch > 0 ? "text-success mr-2":"text-danger mr-2"}>{coalData.ch}$</p>
                            <p id="header-modal-ch" className={coalData.chp > 0 ? "text-success mr-2":"text-danger mr-2"}>{coalData.chp}</p>
                        </div>
                    </div>
                    <div className="header-modal3">
                        <p id="header-modal-title">Coal</p>
                        <p id="header-modal-price">{bitcoinData.current_price}$</p>
                        <div className="pricechange">
                            <p id="header-modal-chp" className={bitcoinData.price_change_24h > 0 ? "text-success mr-2":"text-danger mr-2"}>{bitcoinData.price_change_24h}$</p>
                            <p id="header-modal-ch" className={bitcoinData.price_change_percentage_24h > 0 ? "text-success mr-2":"text-danger mr-2"}>{bitcoinData.price_change_percentage_24h}</p>
                        </div>
                    </div>
                    <div className="header-modal4">
                        
                    </div>
                </div>
            </header>
        </div>
    )
}

export default Main;
